import {CHANGE_TOTAL_SCORE, SUBMITTED_FALSE, SUBMITTED_TRUE, DELETE_QUESTION, ADD_QUESTION, ADD_QUESTION_SUBMITTED, IS_ANSWER_VALID, IS_QUESTION_VALID} from './actionTypes'

export function changeTotalScore(score) {
  return {
    type: CHANGE_TOTAL_SCORE,
    score
   }
}

export function submittedFalse() {
  return {
    type: SUBMITTED_FALSE,
   
  }
}

export function submittedTrue() {
  return {
    type: SUBMITTED_TRUE,
   
  }
}

export function addQuestion() {
  return {
    type: ADD_QUESTION,
  }
}

export function deleteQuestion(id, score) {
  return {
    type: DELETE_QUESTION,
    id,
    score,
   
  }
}

export function addQuestionSubmitted() {
  return {
    type: ADD_QUESTION_SUBMITTED,
    
  }
}

export function isAnswerValid(valid) {
  return {
    type: IS_ANSWER_VALID,
    valid
  }
}

export function isQuestionValid(valid) {
  return {
    type: IS_QUESTION_VALID,
    valid
    
  }
}
