import React from 'react';
import styled from 'styled-components'

const AnswersField = styled.textarea`
  width: 100%;
`;

export default class OneAnswerCreater extends React.Component {
  constructor(props){
     super(props);
     this.state = {title: ''}
  }
  inputChangeHandler = (e) => {
    return new Promise((resolve, reject) => {
      this.setState({title: e.target.value});
      resolve(this.state.title);
    }) 
    .then(title => {
      this.getInputValue();
    }) 
  }
  getInputValue = () => {
    let answers = this.props.answers.map(answer => {
      if (answer.id === this.props.id) {
        answer.title = this.clearWordFromSpaces(this.state.title);
        answer.isRight = this.props.isRight; 
      } 
      return answer; 
    })
    this.props.setAnswersValues(answers);
  }
  clearWordFromSpaces = (word) => {
    return  word && word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
  }
  componentDidUpdate(prevProps) {
    prevProps.isRight !== this.props.isRight && this.getInputValue();
  }
 	render() {
    return ( 
      <AnswersField 
        required
        id={this.props.id} 
        value={this.title} 
        onChange={this.inputChangeHandler}
        rows='2'
       
      />
    )
  }
}
