import React, { Component } from 'react';
import styled, {css} from 'styled-components'
import { connect } from 'react-redux';
import 'react-sticky-header/styles.css';
import { StickyContainer, Sticky } from 'react-sticky';
import Button from '../../components/QuizCreator/Button';
import TestTitle from '../../components/QuizCreator/TestInputs/TestTitle';
import TestCreatorCompany from '../../components/QuizCreator/TestInputs/TestCreatorCompany';
import TestType from '../../components/QuizCreator/TestInputs/TestType';
import TestDeadline from '../../components/QuizCreator/TestInputs/TestDeadline';
import TestDuration from '../../components/QuizCreator/TestInputs/TestDuration';
import TestDescription from '../../components/QuizCreator/TestInputs/TestDescription';

const Main = styled.div`
	margin: auto;
	max-width: 1200px;
	font-size: 24px;
	overflow: auto;
`;

const FlexRow = styled.div`
	display: flex;
	position: relative;
	flex-direction: row;
	flex-wrap: wrap;
	margin: 30px 0;
	justify-content: space-between;
	width: ${props => props.width || '100%'};
	box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
		min-width: 100%;
	}
`;

class TestHeader extends Component {
	constructor(props) {
		super(props);
		this.state = {
			testTitle: '',
			description: '',
			testDeadline: '',
			testType: '',
			company: '',
            testDuration: '',
            
		}
	}
    getInputValue = (e, field) => {
        let value = e.target.value;
        value = this.clearWordFromSpaces(value);

        if (e.target.type === 'number') {
            if (e.target.value < 0) {
                value = +e.target.value * (-1);
            } else if (e.target.value > 0) {
                value = +e.target.value;
            } else {
                value = '';
            }
        }

        this.setState({ [field]: value });
    }

    clearWordFromSpaces = (word) => {
		return word.replace(/^[ ]+/g, '').replace(/\s*$/, '');
    }
    
    checkInputValidation = (e) => {
        let placeholder =  e.target.placeholder
        let invalidPlaceholder = `Fill ${placeholder}`;

        e.target.placeholder = !e.target.value 
            ? invalidPlaceholder 
            : placeholder.replace('Fill ','');
    }
    
    isFilled = (inputValue) => {
        console.log(inputValue);
        return !inputValue ? true : false;
	}
  	
	componentDidUpdate(prevProps, prevState) {
	
	}
	
	render() {
		return (
            <Main>
                <FlexRow>
                    <TestTitle
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testTitle}
                        isFilled={this.isFilled}
                    />
                    <TestCreatorCompany 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.company}
                        isFilled={this.isFilled} 
                    />
                    <TestType 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testType}
                        isFilled={this.isFilled} 
                    />
                </FlexRow>
                <FlexRow>
                    <TestDescription 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.description}
                        isFilled={this.isFilled}
                    />
                </FlexRow>
                <FlexRow>
                    <TestDeadline 
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testDeadline}
                        isFilled={this.isFilled} 
                    />
                    <TestDuration
                        validation={this.checkInputValidation}
                        getInputValue={this.getInputValue}
                        value={this.state.testDuration}
                        isFilled={this.isFilled}  
                    />
                </FlexRow>
            </Main>
		);
	}
}

function mapStateToProps(state) {
	return {
		totalScore: state.test.totalScore,
		submitted: state.test.submitted,
	}
}

function mapDispatchToProps(dispatch) {
	return {
		
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(TestHeader)
