import React, { Component } from 'react';
import { connect } from 'react-redux';
import TestRender from '../TestRenderComponents/TestRender';
import styled, {css} from 'styled-components'

let percent;
const styles = { transition: 'all 1s linear' }

function resp(){
	if (window.innerWidth <= 500) {
		percent = 90
	} else {
		percent = 70
	}
}

resp()

window.onresize=function(){ resp() }


// styled carousel

const CarouselContainer = styled.div`
  width: 940px;
  max-width: 100%;
  box-sizing: border-box;
  position: relative;
  overflow: hidden;
`
const ContainerItem = styled.div`
  position: relative;
  display: flex;
  width: 3200px;
  max-width: 333.3%;
  padding: 5px;
  box-sizing: border-box;
  @media screen and (max-width: 500px){
    max-width: unset;
    width: 1000%;
  }
`
const Arrow = styled.span`
  display: inline-block;
  margin: 15px;
  cursor: pointer;
  font-size: 24px;
  font-weight: bold;
`
const FlexRow = styled.div`
	display: flex;
	margin: auto;
    max-width: 1200px;
    flex-direction: row;
	justify-content: ${props => props.content || 'space-between'};
	align-items: center;
	flex-wrap: wrap;
    box-sizing: border-box;

	@media screen and (max-width: 1190px) {
		flex-direction: column;
        min-width: 100%;
    }
`;

const FlexChild = styled.div`
	position: relative;
    width: ${props => props.width || ''};
    height: ${props => props.height || ''};    
    box-sizing: border-box; 
    
	@media screen and (max-width: 1190px) {
		margin: 20px 5px;
		max-width: 98%;
	}
`;
const Test = styled.div`
	margin: 20px;
`;

class TestSlider extends Component {
	constructor(props){
		super(props);

		this.state = {
			translate : 0,
			tests: this.props.tests,
		}
    }
    setImage = () => {

	}

	componentDidUpdate(prevProps, prevState) {
        if (this.props.tests === true && this.props.tests !== prevProps.tests.testsLoaded) {
            this.setState({tests: this.props.tests});
        }
	}
	
	componentDidMount () {

		setInterval(() => {
			this.setState({ translate : this.state.translate === -percent ? 0 : this.state.translate - 10 })
		}, 10000)
	}

	ToLeft () {
		this.setState({ translate : this.state.translate === 0 ? -percent : this.state.translate + 10 })
	}	

	ToRight () {
		this.setState({ translate : this.state.translate === -percent ? 0 : this.state.translate - 10 })
	}

	getTodayDate = () => {
		let today = new Date();
		let dd = today.getDate();
		let mm = today.getMonth() + 1;
		let yyyy = today.getFullYear();

		if(dd < 10) {
			dd = '0' + dd
		} 

		if (mm < 10) {
			mm = '0' + mm
		} 
		return	today = yyyy + '-' + mm + '-' + dd;
	}

	compareDates = (stringDate) => {
		let today = new Date();
		today = this.getTodayDate(today);
		return Date.parse(stringDate) >= Date.parse(today);        
	}

	render() {
		let tests = [];
		let fillteredTests = [];
		if (this.state.tests) {
			tests = Object.values(this.state.tests[0]);
			fillteredTests = tests.filter(item => {
				if (this.compareDates(item.testDeadline)) {
					return item;
				}
			})
		} 
		return (
			<>
				<FlexRow>
					<FlexChild>
						<Arrow onClick={this.ToLeft.bind(this)}> {'<'} </Arrow>
					</FlexChild>
					<FlexChild width={'960px'}>
						<CarouselContainer>
							<ContainerItem style={{ ...styles, transform: `translateX(${this.state.translate}%)` }}>
								{this.state.tests 
									?	fillteredTests.map( (item, index) => {
											return (
												<Test>
													<TestRender test={item} key={item.id}/>
												</Test>
											);
										})
									
									: ''
								}
							</ContainerItem>
						</CarouselContainer>
					</FlexChild>
					<FlexChild>
						<Arrow onClick={this.ToLeft.bind(this)}> {'>'} </Arrow>
					</FlexChild>
				</FlexRow>
			</>
		);
	}
}

function mapStateToProps(state) {
	return {
		tests: state.appReducer.tests,
	}
	
}

export default connect(mapStateToProps, null)(TestSlider)
